-- Test for issue #84896: Incorrect autogenerated column name for Bool literals
-- This test ensures that Bool literals generate distinct column names from integer literals

SELECT toTypeName(0), toTypeName(false);
SELECT toTypeName(1), toTypeName(true);

SELECT 
    toTypeName(true) as bool_true_type,
    toTypeName(false) as bool_false_type,
    toTypeName(0) as int_zero_type,
    toTypeName(1) as int_one_type;
